import fav from '../images/unfavorite.svgfavorite.svg';
import unfav from '../images/unfavorite.svg';

function FavButton( { movie, remove, handleFavClick }) {
    function handleAddFav() {
        handleFavClick(true, movie);
    }

    function handleRemoveFav() {
        handleFavClick(false, movie);
    }

    return (
        <>
            {remove === false ? 
            <div className="fav-btn" ><img onClick={handleAddFav} src={ fav } alt="Solid heart"/></div> : 
            <div className="fav-btn"><img className="fav-btn" onClick={handleRemoveFav}src={ unfav } alt="Empty heart"/></div>}
        </>
    );
}

FavButton.defaultProps = {
    remove: false
}

export default FavButton;
